import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Row, Col, Card } from 'react-bootstrap';

import { deletePostById } from "../../actions";

export default function PostCard({ post }) {

    const dispatch = useDispatch();

    const posts = useSelector(state => state.posts);


    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [title, setTitle] = useState("");
    const [body, setBody] = useState("");

    const [content, setContent] = useState('');

    const handlePostDelete = (e, postId) => {
        e.preventDefault();
        console.log(postId)
        dispatch(deletePostById(postId)).then(() => {
            console.log(posts)
        });
    }

    const handlePostEdit = (e, postId) => {
        e.preventDefault();
        console.log(postId)
    }

    const handleSubmit = (e, postId) => {
        e.preventDefault();
        console.log(content, postId)
    }

    return (<div>
        <Card style={{ width: '18rem' }}>
            <Card.Header as="h5">
                <Row>
                    <Col>
                        {post.title}
                    </Col>
                    <Col xs lg="5" className="d-flex">
                        <button
                            className="btn"
                            type="button"
                            onClick={(e) => {
                                handlePostEdit(e, post.id);
                            }}
                        >
                            <i className="far fa-edit"></i>
                        </button>
                        <button
                            className="btn ml-1"
                            type="button"
                            onClick={(e) => {
                                handlePostDelete(e, post.id);
                            }}
                        >
                            <i className="fa fa-trash"></i>
                        </button>

                    </Col>
                </Row>
            </Card.Header>
            <Card.Body>
                <Card.Text>{post.body}</Card.Text>
            </Card.Body>
            <div className="input-group">
                <input type="text"
                    className="form-control"
                    placeholder="comment"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                    value={content}
                    onChange={e => setContent(e.target.value)} />
                <div className="input-group-append">
                    <button
                        className="btn btn-outline-secondary"
                        type="button"
                        disabled={content ? false : true}
                        onClick={(e) => {
                            handleSubmit(e, post.id);
                        }}
                    >
                        <i className="far fa-comment"></i>
                    </button>
                </div>
            </div>
        </Card>

        
    </div>);
}
