import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Row, Col, Card, Container, FormLabel, Modal, Button } from 'react-bootstrap';

import { deletePostById, editPostById } from "../../actions";

export default function PostCard({ post }) {

    const dispatch = useDispatch();

    const posts = useSelector(state => state.posts);


    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [title, setTitle] = useState(post.title);
    const [content, setContent] = useState(post.content);

    const [comment, setComment] = useState('');

    const handlePostDelete = (e, postId) => {
        e.preventDefault();
        console.log(postId)
        dispatch(deletePostById(postId)).then(() => {
            console.log(posts)
        });
    }

    const handlePostEdit = (e, postId) => {
        e.preventDefault();
        console.log(postId)
    }

    const handleComment = (e, postId) => {
        e.preventDefault();
        console.log(comment, postId)
    }

    const handleSubmitEdit = (e, postId) => {
        if (title && content) {
            dispatch(editPostById(postId, { title: title, body: content })).then(() => handleClose());
        }
    }


    return (<div>
        <Card style={{ width: '18rem' }}>
            <Card.Header as="h5">
                <Row>
                    <Col>
                        {post.title}
                    </Col>
                    <Col xs lg="5" className="d-flex">
                        <button className="btn ml-1" type="button" onClick={handleShow} >
                            <i className="far fa-edit"></i>
                        </button>
                        <button className="btn" type="button" onClick={(e) => { handlePostDelete(e, post.id); }} >
                            <i className="fa fa-trash"></i>
                        </button>

                    </Col>
                </Row>
            </Card.Header>
            <Card.Body>
                <Card.Text>{post.body}</Card.Text>
            </Card.Body>
            <div className="input-group">
                <input type="text"
                    className="form-control"
                    placeholder="comment"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                    value={comment}
                    onChange={e => setComment(e.target.value)} />
                <div className="input-group-append">
                    <button
                        className="btn btn-outline-secondary"
                        type="button"
                        disabled={comment ? false : true}
                        onClick={(e) => {
                            handleComment(e, post.id);
                        }}
                    >
                        <i className="far fa-comment"></i>
                    </button>
                </div>
            </div>
        </Card>

        <Modal show={show} onHide={handleClose}>
            <Container>

                <div className='post-model-title'>
                    <FormLabel>Edit Post</FormLabel>
                    <Button
                        variant="primary"
                        type="button"
                        size="sm"
                        onClick={(e) => {
                            handleSubmitEdit(e, post.id);
                        }}
                    >
                        Submit
                    </Button>
                </div>

                <div className="input-group mb-3">
                    <div className="input-group-prepend">
                        <span className="input-group-text" id="basic-addon1">Title</span>
                    </div>
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Post Title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        aria-label="Post Title"
                        aria-describedby="basic-addon1" />
                </div>

                <div className="input-group">
                    <div className="input-group-prepend">
                        <span className="input-group-text">Content</span>
                    </div>
                    <textarea
                        className="form-control"
                        placeholder="Post Content"
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        aria-label="Post Content"
                    ></textarea>
                </div>


            </Container>
            <br />
        </Modal>
    </div>);
}
